mapper.select:
SELECT id
     , route_id
     , route_grade
     , seat_grade
     , charge
     , status
FROM charge
WHERE route_id = #{routeId}
AND route_grade = #{routeGrade}
AND seat_grade = #{seatGrade}

mapper.update:
UPDATE charge
SET route_grade = #{routeGrade}
AND seat_grade = #{seatGrade}
AND charge = #{charge}
WHERE route_id = #{routeId}
